var EditUI = Class.extend({
	construct: function(config) {
		this.visible = false;
	},
	bindTo: function(target) {
		if(target.EditUI) {
			return ;
		}
		target.EditUI = this;
		var t = $(target),
			that = this,
			text = t.find(".toggle-edit-buttons-text")
		;
		this.visibleText = t.data("visible-text");
		this.hiddenText = t.data("hidden-text");
		this.text = text;
		t.on("click", function(e) {
			that.toggle();
			/*if(e.preventDefault) {
				e.preventDefault();
			}
			return false;*/
		});
	},
	toggle: function() {
		if(this.visible) {
			this.visible = false;
			this.text.text(this.hiddenText);
			$(".edit-button").addClass("hidden");
		} else {
			this.visible = true;
			this.text.text(this.visibleText);
			$(".edit-button").removeClass("hidden");
		}
	}
});
EditUI.init = function() {
	$("[data-toggle-edit-buttons='true']").each(function(){
		new EditUI().bindTo(this);
	});
	$('.edit-button').tooltip({});
}
return EditUI;
