var ProgressBar = ViewComponent.extend({
	construct: function (config) {
		if (!config) {
			config = {};
		}
		if (!config.percent) {
			config.percent = 0;
		}

		if (config.percent > 100) {
			config.percent = 100;
		} else {
			config.percent = 0;
		}
		this.callSuper(arguments, config);
	},
	renderTo: function (renderTarget) {
		this.wrapper = $(renderTarget).append("<div class=\"progress\"></div>").children().last();
		if (this.hidden) {
			this.hide();
		}
		this.bar = $(this.wrapper).append("<div class=\"progress-bar\" role=\"progressbar\"></div>").children().last();
		this.setPercent(this.percent);
	},
	hide: function () {
		$(this.wrapper).hide();
	},
	show: function () {
		$(this.wrapper).show();
	},
	setPercent: function (percent) {
		this.percent = percent;
		if (this.bar) {
			$(this.bar).width(this.percent + "%");
		}
	},
	none: function(msg) {
		this._status("", msg);
	},
	success: function(msg) {
		this._status("bar-success", msg);
	},
	warn: function(msg) {
		this._status("bar-warning", msg);
	},
	error: function(msg) {
		this._status("bar-danger", msg);
	},
	_status: function (cls, msg) {
		this.bar.attr("class", "bar "+cls);
		if(msg) {
			this.bar.text(msg);
		}
	}
});
return ProgressBar;
