var Task = Observable.extend({
	construct: function(config) {
		if(!config) {
			config = {};
		}
		this.callSuper(arguments, config);
	},
	setHandler: function(fn, scope) {
		this.fn = fn;
		this.scope = scope;
	},
	_execute: function(){
		if(this.scope) {
			this.fn.call(this.scope);
		} else {
			this.fn();
		}
	},
	cancel: function(){
		this.cancelled = true;
	},
	delay: function(millis){
		var _this = this;
		setTimeout(function(){
			if(!_this.cancelled) {
				_this._execute();
			} else {
				_this.cancelled = false;
			}
		}, millis);
	}
});
return Task;
