var Value = Observable.extend({
	construct: function(config) {
		if (!config) {
			config = {};
		}
		this.callSuper(arguments, config);
		this.set(config.value);
	},
	set: function(newValue, silent) {
		var oldValue = this._v;
		this._v = newValue;
		if(!silent) {
			this.fireEvent("changed", newValue, oldValue, this);
		}
		return this;
	},
	get: function() {
		return this._v;
	}
});
return Value;
