var Link = ViewComponent.extend({
	construct: function(config) {
		if (!config) {
			config = {};
		}
		if(!config.title) {
			config.title = config.label;
		}
		if(!config.title) {
			config.title = "download";
		}
		this.callSuper(arguments, config);
	},
	renderTo: function(renderTarget) {
		this.element = $(renderTarget).append("<a></a>").children().last();
		$(this.element).attr("href", this.url);
		this.label = new Text({value: this.title});
		this.label.renderTo($(this.element));
		this.rendered = true;
		if(this.hidden) {
			this.hide();
		}
	},
	setUrl: function(url){
		if(!url) {
			url = "#";
		}
		this.url = url;
		if(this.element) {
			$(this.element).attr("href", url);
		}
	},
	hide: function(){
		if(this.element) {
			$(this.element).hide();
		}
	},
	show: function(){
		if(this.element) {
			$(this.element).show();
		}
	},
	destroy: function() {
		this.invokeSuper(arguments);
		if (this.element) {
			this.label.destroy();
			$(this.element).remove();
		}
	}
});
return Link;
