var Form = InputViewContainer.extend({
	construct: function(config) {
		if (!config) {
			config = {};
		}
		this.callSuper(arguments, config);
	},
	renderTo: function(renderTarget) {
		this.element = $(renderTarget).append("<form></form>").children().last();
		$(this.element).submit(function() {
			// don't do a submit
			return false;
		});
		this.rendered = true;
		this.renderItems();
		if (this.hidden) {
			this.hide();
		}
	},
	hide: function() {
		if (this.element) {
			$(this.element).hide();
		}
	},
	show: function() {
		if (this.element) {
			$(this.element).show();
		}
	}
});
return Form;
