var Button = ViewComponent.extend({
	construct: function (config) {
		if (!config) {
			config = {};
		}
		this.callSuper(arguments, config);
	},
	destroy: function () {
		this.invokeSuper(arguments);
		if (this.element) {
			$(this.element).remove();
		}
	},
	enable: function () {
		this.disabled = false;
		if (this.element) {
			$(this.element).removeClass("disabled");
		}
	},
	disable: function () {
		this.disabled = true;
		if (this.element) {
			$(this.element).addClass("disabled");
		}
	},
	renderTo: function (renderTarget) {
		var href = this.href;
		if (!href) {
			href = "#";
		}
		this.element = $(renderTarget).append("<a class=\"btn btn-default\"></a>").children().last();
		$(this.element).attr("href", href);
		this.labelText = new Text({value: this.label});
		this.labelText.renderTo(this.element);
		$(this.element).click(this._createClickListener());
		if (this.disabled) {
			this.disable();
		}
		if (this.hidden) {
			this.hide();
		}
	},
	setLabel: function(label) {
		this.label = label;
		if(this.labelText) {
			this.labelText.value.set(this.label);
		}
	},
	_createClickListener: function () {
		var _this = this;
		return function () {
			if (!this.disabled) {
				_this.fireEvent("clicked", _this);
			}
		};
	},
	show: function() {
		this.hidden = false;
		if(this.element) {
			this.element.show();
		}
	},
	hide: function() {
		this.hidden = true;
		if(this.element) {
			this.element.hide();
		}
	}
});
return Button;
