var SearchTermInput = TextInput.extend({
	construct: function (config) {
		if (!config) {
			config = {};
		}
		this.callSuper(arguments, config);
	},
	destroy: function () {
		this.invokeSuper(arguments);
	},
	renderTo: function (renderTarget) {
		var _this = this;
		this.form = $(renderTarget).append("<form>").children().last();
		$(this.form).submit(function () {
			return false;
		});
		this.wrapper = $(this.form).append("<div class=\"form-group\"><div class=\"input-group js-input-group\"><div class=\"input-group-addon\"><span class=\"glyphicon glyphicon-search\" aria-hidden=\"true\"></span></div></div></div>").children().last().find(".js-input-group");
		this.callSuper(arguments, this.wrapper);
		$(this.input).keyup(function (event) {
			_this.keyup(event);
		});
	},
	submitSearch: function () {
		this.fireEvent("searchTermSubmitted", this.getValue(), this);
		return false;
	},
	keyup: function (event) {
		// Enter key = keycode 13
		if (event.which === 13) {
			this.submitSearch();
		}
		return false;
	}
});
return SearchTermInput;
