var TextInput = InputViewComponent.extend({
	construct: function(config) {
		if (!config) {
			config = {};
		}
		if (!config.type) {
			config.type = "text";
		}
		if (config.type === "number") {
			if (!config.step) {
				config.step = "0.01";
				if (config.entity && config.member) {
				}
			}
		}
		this.callSuper(arguments, config);

	},
	destroy: function() {
		arguments.callee.$parent.destroy.call(this);
	},
	renderTo: function(renderTarget) {
		this.input = $(renderTarget).append("<input class=\"form-control\"/>").children().last();
		$(this.input).attr("type", this.type);
		if (this.placeholder) {
			$(this.input).attr("placeholder", this.placeholder);
		}
		if (this.disabled) {
			$(this.input).attr("disabled", "");
		}
		if (this.id) {
			$(this.input).attr("id", this.id);
		}
		if (this.step) {
			$(this.input).attr("step", this.step);
		}
		if (this.min !== undefined && this.min !== null) {
			$(this.input).attr("min", this.min);
		}
		if (this.max !== undefined && this.max !== null) {
			$(this.input).attr("max", this.max);
		}
		if (this.name) {
			$(this.input).attr("name", this.name);
		}
		if (this.cssClass) {
			$(this.input).addClass(this.cssClass);
		}
		var bv = this.getBoundValue();
		if (bv !== undefined && bv !== null) {
			this.setValue(bv);
		}

		var _this = this;
		$(this.input).change(function() {
			_this.inputChanged();
		});
	},
	getValue: function() {
		return $(this.input).val();
	},
	setValue: function(value) {
		if (value === undefined || value === null) {
			value = "";
		}
		$(this.input).val(value);
	},
	enable: function() {
		this.disabled = false;
		if (this.input) {
			$(this.input).removeAttr("disabled");
		}
	},
	disable: function() {
		this.disabled = true;
		if (this.input) {
			$(this.input).attr("disabled", "");
		}
	}
});
InputTypeRegistry.register("TextInput", TextInput);
return TextInput;
