var RequireView = Class.extend({
	construct: function(config) {
	},
	bindTo: function(target) {
		if(target.RequireView) {
			return ;
		}
		target.RequireView = this;
		var t = $(target),
			url = t.data("view-impl")
		;
		require([url], function(viewClass) {
			new viewClass().renderTo(t);
		});
	}
});
RequireView.init = function() {
	$(".require-view[data-view-impl]").each(function(){
		new RequireView().bindTo(this);
	});
}
return RequireView;
