var TwoColumnView = ViewComponent.extend({
	construct: function(config) {
		if (!config) {
			config = {};
		}
		this.callSuper(arguments, config);
	},
	destroy: function() {
		this.invokeSuper(arguments);
	},
	renderTo: function(renderTarget) {
		this.container = $(renderTarget).append("<div class=\"container-fluid\" />").children().last();
		this.row = $(this.container).append("<div class=\"row-fluid\" />").children().last();
		this.leftColumn = $(this.row).append("<div class=\"span2\" />").children().last();
		this.rightColumn = $(this.row).append("<div class=\"span10\" />").children().last();
	}
});
return TwoColumnView;
